def find_highest_values_and_indexes(x, y):
    max_x = x[0][0]
    max_y = y[0][0]
    index_x = (0, 0)
    index_y = (0, 0)
    
    # Find highest value and its index in array x
    for i in range(len(x)):
        for j in range(len(x[i])):
            if x[i][j] > max_x:
                max_x = x[i][j]
                index_x = (i, j)
                
    # Find highest value and its index in array y
    for i in range(len(y)):
        for j in range(len(y[i])):
            if y[i][j] > max_y:
                max_y = y[i][j]
                index_y = (i, j)
                
    return (max_x, index_x), (max_y, index_y)



